select `Towar`.`ID` AS `ID`,`Towar`.`Indeks` AS `Indeks`,`Towar`.`Nazwa` AS `Nazwa`,`Towar`.`Ilosc` AS `Ilosc`,`Towar`.`ID_magazyn` AS `ID_magazyn`,`Towar`.`Cena` AS `Cena`,`Towar`.`Jednostka` AS `Jednostka`,`Towar`.`ID_rodzaj` AS `ID_rodzaj`,`Towar`.`Ilosc_min` AS `Ilosc_min`,`Towar`.`Ilosc_max` AS `Ilosc_max` from `Towar` where ((`Towar`.`Indeks` like 'A%') and (`Towar`.`Cena` between 100 and 500) and (`Towar`.`Ilosc` <> 0));
select `Magazyn`.`Nazwa` AS `Nazwa`,`Rodzaj_Magazyn`.`Nazwa` AS `Rodzaj Magazynu`,sum(`Towar`.`Ilosc`) AS `Ilosc Towaru` from ((`Magazyn` join `Towar` on((`Magazyn`.`ID` = `Towar`.`ID_magazyn`))) join `Rodzaj_Magazyn` on((`Magazyn`.`ID_rodzaj` = `Rodzaj_Magazyn`.`ID`))) group by `Towar`.`ID_magazyn` ;
select `Towar`.`ID` AS `ID`,`Towar`.`Indeks` AS `Indeks`,`Towar`.`Nazwa` AS `Nazwa`,`Towar`.`Ilosc` AS `Ilosc`,`Towar`.`ID_magazyn` AS `ID_magazyn`,`Towar`.`Cena` AS `Cena`,`Towar`.`Jednostka` AS `Jednostka`,`Towar`.`ID_rodzaj` AS `ID_rodzaj`,`Towar`.`Ilosc_min` AS `Ilosc_min`,`Towar`.`Ilosc_max` AS `Ilosc_max` from `Towar` having (`Towar`.`Cena` < avg(`Towar`.`Cena`)) ;
select `Towar`.`Nazwa` AS `Nazwa`,sum(`Obsluga_dok`.`Ilosc_tow`) AS `SUMA` from (`Obsluga_dok` join `Towar` on((`Obsluga_dok`.`ID_towar` = `Towar`.`ID`))) group by `Obsluga_dok`.`ID_towar` order by sum(`Obsluga_dok`.`Ilosc_tow`) desc limit 10 ;
select `Obsluga_dok`.`Nr_dok` AS `Nr_dok`,sum((`Towar`.`Cena` * `Obsluga_dok`.`Ilosc_tow`)) AS `Wartosc`,cast(now() as date) AS `DATE(NOW())`,NULL AS `NULL` from (`Obsluga_dok` join `Towar` on((`Obsluga_dok`.`ID_towar` = `Towar`.`ID`))) group by `Obsluga_dok`.`Nr_dok` ;
select `Rodzaj_Towar`.`Nazwa` AS `Nazwa`,sum(`Towar`.`Cena`) AS `Wartość` from (`Towar` join `Rodzaj_Towar` on((`Towar`.`ID_rodzaj` = `Rodzaj_Towar`.`ID`))) group by `Towar`.`ID_rodzaj` ;
select `Obsluga_dok`.`Nr_dok` AS `Nr_dok`,`Dokumenty`.`Nazwa` AS `Dokument`,`Towar`.`Nazwa` AS `Towar`,`Obsluga_dok`.`Ilosc_tow` AS `Ilosc_tow` from ((`Obsluga_dok` join `Dokumenty` on((`Obsluga_dok`.`ID_dok` = `Dokumenty`.`ID`))) join `Towar` on((`Obsluga_dok`.`ID_towar` = `Towar`.`ID`))) where (`Obsluga_dok`.`ID_dok` = 2) ;
select avg(`Raport_dok`.`Wartosc`) AS `Średania Wartość`,month(`Raport_dok`.`Data_wystawienia`) AS `Miesiąc`,year(`Raport_dok`.`Data_wystawienia`) AS `Rok` from `Raport_dok` group by month(`Raport_dok`.`Data_wystawienia`) ;
select `Towar`.`Nazwa` AS `Nazwa`,`Towar`.`Ilosc` AS `Ilosc`,`Towar`.`Ilosc_min` AS `Ilosc_min`,(`Towar`.`Ilosc_max` - `Towar`.`Ilosc`) AS `Nalezy zamowic` from `Towar` where (`Towar`.`Ilosc` < `Towar`.`Ilosc_min`) ;
select `Zamówiony Towar`.`Towar` AS `Towar`,avg(`Zamówiony Towar`.`Ilosc_tow`) AS `AVG(Ilosc_tow)` from `Zamówiony Towar` group by `Zamówiony Towar`.`Towar` ;
